    private void okActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okActionPerformed
        double ro;
        double a;
        int adivinha;
        int arend;

        a = Math.random();
        ro = (1+a*(11-1));
        arend = (int) Math.round(ro);

        adivinha = Integer.parseInt(campotxt.getText());

        valorc.setText(Integer.toString(arend));

        if(adivinha == arend){

            resposta.setText("ACERTASTE O PALPITE");

        }
        else{
            resposta.setText("ERRASTE O PALPITE");
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_okActionPerformed

