    private void btndividActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndividActionPerformed
      int v1;
      int v2;
      float a;
      float b;
      
      v1 = Integer.parseInt(txtnu.getText());
      v2 = Integer.parseInt(txtde.getText());
      
      a = v1/v2;
      b = (float) Math.sqrt(v1);
      
      lbldiv.setText(Float.toString(a));
      lblres.setText(Float.toString(b));
// TODO add your handling code here:
    }//GEN-LAST:event_btndividActionPerformed

